package com.java.second;

public class TestDataTypes {

    public static void main(String[] args) {
        // TODO Auto-generated method stub
        byte range;
        range = 124;
        System.out.println("Java byte data type:"+range);

        short temperature;
        temperature = -200;
        System.out.println("Java short data type:"+temperature);
        
        int a,b;
        a= -200000;
        b=100000;
        System.out.println("Integer a:"+a+"\n"+"Integer b:"+b);
        
        long c = -42332200000L;
        System.out.println("Java long data type:"+c);  
        
        float num1 = -42.3f;
        System.out.println("Java float data type:"+num1);
        
        double num2 = -42.3;
        System.out.println("Java double data type:"+num2);
        
        boolean flag = true;
        System.out.println("Java boolean data type:"+flag);
        
        char letter1 = '\u0051';
        System.out.println("Java char data type:"+letter1);
        
        char letter2 = '9';
        System.out.println("Java char data type:"+letter2); 
            
        char letter3 = 65;
        System.out.println("Java char data type:"+letter3);
    }

}
