package com.java.third;

public class Test2 {

    public static void main(String[] args) {
        // TODO Auto-generated method stub
        System.out.println(" Assignment Operators");
        System.out.println("-----------------");
        // create variables
        int a = 4;
        int var;

        var = a;
        System.out.println("var using =: " + var);
        
        var += a;
        System.out.println("var using +=: " + var);
     
        var *= a;
        System.out.println("var using *=: " + var);
    }

}
